export const calculateShortestPath = async (start, end, userRoute) => {
  try {
      const response = await fetch('/api/calculate-shortest-path', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify({
              start,
              end,
              userRoute,
          }),
      });

      if (!response.ok) {
          const error = await response.text(); // Log error text for debugging
          console.error('Backend error:', error);
          throw new Error('Failed to calculate shortest path');
      }

      return response.json(); // Parse the response as JSON
  } catch (error) {
      console.error('Error in calculateShortestPath:', error);
      throw error;
  }
};
