import React, { useCallback, useRef } from 'react';
import { GoogleMap, LoadScript, Polyline } from '@react-google-maps/api';

const containerStyle = {
    width: '50%',
    height: '400px',
};

function Map({ routes }) {
    const mapRef = useRef(null);

    const onLoad = useCallback((map) => {
        mapRef.current = map;

        // Create bounds for the entire continental US
        const bounds = new window.google.maps.LatLngBounds(
            { lat: 24.396308, lng: -125.0 }, // Southwest corner
            { lat: 49.384358, lng: -66.93457 } // Northeast corner
        );

        // Fit the map to these bounds
        map.fitBounds(bounds);
    }, []);

    // Colors for each route
    const routeColors = ['#0000FF', '#FF0000', '#008000']; // Blue, Red, Green

    return (
        <LoadScript googleMapsApiKey="AIzaSyAWKY1m0h2NlZh6_WBgowVA87u516kWVfQ">
            <GoogleMap
                mapContainerStyle={containerStyle}
                onLoad={onLoad}
                options={{
                    disableDefaultUI: true, // Removes default controls
                    zoomControl: true, // Enable zoom control
                    minZoom: 4, // Set minimum zoom level
                    maxZoom: 20, // Set maximum zoom level
                }}
            >
                {/* Map over routes to display polylines */}
                {routes &&
                    routes.slice(0, 3).map((route, index) => (
                        <Polyline
                            key={index}
                            path={route.map((airport) => ({
                                lat: airport.lat,
                                lng: airport.long,
                            }))}
                            options={{
                                strokeColor: routeColors[index] || '#000000', // Default to black for extra routes
                                strokeOpacity: 0.7,
                                strokeWeight: 5,
                            }}
                        />
                    ))}
            </GoogleMap>
        </LoadScript>
    );
}

export default Map;
