import React, { useState } from 'react';
import { LoadScript } from '@react-google-maps/api';
import AirportDropdown from './components/AirportDropdown';
import RouteForm from './components/RouteForm';
import Map from './components/Map';
import ResultTable from './components/ResultTable';
import airports from './data/airports.json';
import './styles.css';

function App() {
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [routeResults, setRouteResults] = useState(null);
    const [selectedRouteCoordinates, setSelectedRouteCoordinates] = useState(null);

    const handleRouteSelect = (routeCoordinates) => {
        setSelectedRouteCoordinates(routeCoordinates);
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyAWKY1m0h2NlZh6_WBgowVA87u516kWVfQ">
            <div className="container">
                <h1>Shortest Flight Path</h1>
                <div className="form-section">
                    <AirportDropdown label="Starting Airport" onSelect={setStart} />
                    <AirportDropdown label="Destination Airport" onSelect={setEnd} />
                    <RouteForm
                        start={start}
                        end={end}
                        onResults={(results) => {
                            setRouteResults(results);
                            setSelectedRouteCoordinates(null); // Reset selected route on new results
                        }}
                    />
                </div>
                {routeResults && (
                    <ResultTable
                        results={routeResults}
                        onRouteSelect={(route) => {
                            const coordinates = route.map((code) => {
                                const airport = airports.find((airport) => airport.IATA === code);
                                return airport ? { lat: airport.LAT, lng: airport.LONG } : null;
                            }).filter(Boolean);
                            handleRouteSelect(coordinates);
                        }}
                    />
                )}
                {routeResults?.userRoute?.route && (
                    <Map
                        route={
                            selectedRouteCoordinates ||
                            routeResults.userRoute.route.map((code) => {
                                const airport = airports.find((airport) => airport.IATA === code);
                                return airport ? { lat: airport.LAT, lng: airport.LONG } : null;
                            }).filter(Boolean)
                        }
                        isUserRoute={!selectedRouteCoordinates} // Specify if it's the user's route
                    />
                )}
            </div>
        </LoadScript>
    );
}

export default App;
