import React, { useCallback, useRef } from 'react';
import { GoogleMap, Polyline, Marker } from '@react-google-maps/api';

const containerStyle = {
    width: '100%',
    height: '700px',
};

function Map({ route, userRoute }) {
    const mapRef = useRef(null);

    const onLoad = useCallback((map) => {
        mapRef.current = map;

        // Fit the map to the route
        if (route && route.length > 0) {
            const bounds = new window.google.maps.LatLngBounds();
            route.forEach((point) => bounds.extend(point));
            map.fitBounds(bounds);
        }
    }, [route]);

    return (
        <GoogleMap
            mapContainerStyle={containerStyle}
            onLoad={onLoad}
            options={{
                disableDefaultUI: true, // Removes default controls
                zoomControl: true,
                minZoom: 2,
                maxZoom: 20,
            }}
        >
            {/* Draw the user's proposed route polyline in blue */}
            {userRoute && userRoute.length > 0 && (
                <Polyline
                    path={userRoute}
                    options={{
                        strokeColor: '#0000FF', // Blue color
                        strokeOpacity: 0.7,
                        strokeWeight: 5,
                    }}
                />
            )}

            {/* Draw the other route polyline in green */}
            {route && route.length > 0 && (
                <Polyline
                    path={route}
                    options={{
                        strokeColor: 'blue', // Green color
                        strokeOpacity: 0.7,
                        strokeWeight: 5,
                    }}
                />
            )}

            {/* Add markers for the airports */}
            {route &&
                route.map((point, index) => (
                    <Marker
                        key={index}
                        position={point}
                        label={{
                            text: (index + 1).toString(),
                            color: 'white'
                        }}
                        title={`Airport ${index + 1}`}
                    />
                ))}
        </GoogleMap>
    );
}

export default Map;
