import React, { useState } from 'react';
import AirportDropdown from './components/AirportDropdown';
import RouteForm from './components/RouteForm';
import Map from './components/Map';
import ResultTable from './components/ResultTable';
import airports from './data/airports.json'; // Import airport data for coordinates
import './styles.css';

function App() {
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [routeResults, setRouteResults] = useState(null);

    // Extract top three routes for the map
    const topThreeRoutes = routeResults
        ? [
              routeResults?.userRoute,
              routeResults?.shortestPath
                  ? { rawRoute: routeResults.shortestPath }
                  : null,
              ...(routeResults?.alternativeRoutes?.slice(0, 1) || []),
          ]
            .filter(Boolean) // Filter out null/undefined routes
            .map((route) =>
                route?.rawRoute
                    ?.map((code) => {
                        const airport = airports.find((airport) => airport.IATA === code);
                        return airport
                            ? { lat: airport.LAT, lng: airport.LONG }
                            : null;
                    })
                    .filter(Boolean) // Remove null values from the route
            )
        : [];

    return (
        <div className="container">
            <h1>Shortest Flight Path</h1>
            <div className="form-section">
                <AirportDropdown label="Starting Airport" onSelect={setStart} />
                <AirportDropdown label="Destination Airport" onSelect={setEnd} />
                <RouteForm
                    start={start}
                    end={end}
                    onResults={(results) => setRouteResults(results)}
                />
            </div>
            {routeResults && <ResultTable results={routeResults} />}
            {topThreeRoutes.length > 0 && <Map routes={topThreeRoutes} />}
        </div>
    );
}

export default App;
