import React, { useCallback, useRef } from 'react';
import { GoogleMap, Polyline, Marker } from '@react-google-maps/api';

const containerStyle = {
    width: '50%',
    height: '400px',
};

function Map({ route }) {
    const mapRef = useRef(null);

    const onLoad = useCallback((map) => {
        mapRef.current = map;

        // Fit the map to the route
        if (route && route.length > 0) {
            const bounds = new window.google.maps.LatLngBounds();
            route.forEach((point) => bounds.extend(point));
            map.fitBounds(bounds);
        }
    }, [route]);

    return (
        <GoogleMap
            mapContainerStyle={containerStyle}
            onLoad={onLoad}
            options={{
                disableDefaultUI: true, // Removes default controls
                zoomControl: true,
                minZoom: 4,
                maxZoom: 20,
            }}
        >
            {/* Draw the route polyline */}
            {route && route.length > 0 && (
                <Polyline
                    path={route}
                    options={{
                        strokeColor: '#0000FF',
                        strokeOpacity: 0.7,
                        strokeWeight: 5,
                    }}
                />
            )}

            {/* Add markers for the airports */}
            {route &&
                route.map((point, index) => (
                    <Marker
                        key={index}
                        position={point}
                        label={(index + 1).toString()} 
                        // Optional: Labels the marker as 1, 2, 3, etc.
                        title={`Airport ${index + 1}`}
                        // Optional: Adds a title to the marker
                        icon={{
                            url: 'https://maps.google.com/mapfiles/kml/shapes/airports.png', // Custom icon
                            scaledSize: new window.google.maps.Size(40, 40), // Adjust the size of the icon
                            

                            
                        }}
                        // make the marker collor red
                        style={{ color: 'red' }}
                    />
                ))}
        </GoogleMap>
    );
}

export default Map;
