import React, { useCallback, useRef, useEffect } from 'react';
import { GoogleMap, LoadScript, Polyline } from '@react-google-maps/api';

const containerStyle = {
    width: '100%',
    height: '400px',
    border: '1px solid #ccc',
    borderRadius: '8px',
};

const center = {
    lat: 39.8283, // Center of the contiguous US
    lng: -98.5795,
};

// Define the bounds for the US
const US_BOUNDS = {
    north: 49.384358, // North boundary
    south: 24.396308, // South boundary
    west: -125.0, // West boundary
    east: -66.93457, // East boundary
};

function Map({ route }) {
    const mapRef = useRef(null);

    const onLoad = useCallback((map) => {
        mapRef.current = map;

        // Fit the map to US bounds on initial load
        map.fitBounds({
            north: US_BOUNDS.north,
            south: US_BOUNDS.south,
            west: US_BOUNDS.west,
            east: US_BOUNDS.east,
        });
    }, []);

    useEffect(() => {
        // Dynamically fit bounds when a route is provided
        if (route && route.length > 0 && mapRef.current) {
            const bounds = new window.google.maps.LatLngBounds();
            route.forEach((airport) => {
                bounds.extend({ lat: airport.lat, lng: airport.long });
            });
            mapRef.current.fitBounds(bounds);
        }
    }, [route]);

    return (
        <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <GoogleMap
                mapContainerStyle={containerStyle}
                center={center}
                zoom={4}
                onLoad={onLoad}
                options={{
                    restriction: {
                        latLngBounds: US_BOUNDS,
                        strictBounds: true, // Prevent dragging outside the US
                    },
                }}
            >
                {/* Draw route if available */}
                {route && route.length > 0 && (
                    <Polyline
                        path={route.map((airport) => ({
                            lat: airport.lat,
                            lng: airport.long,
                        }))}
                        options={{
                            strokeColor: '#0000FF',
                            strokeOpacity: 0.7,
                            strokeWeight: 5,
                        }}
                    />
                )}
            </GoogleMap>
        </LoadScript>
    );
}

export default Map;
