import React, { useRef, useEffect } from 'react';
import { LoadScript } from '@react-google-maps/api';

function Map({ route }) {
    const mapRef = useRef(null);

    useEffect(() => {
        // Initialize the map
        const latlng = new google.maps.LatLng(37.09024, -95.712891); // Center of the US
        const myOptions = {
            zoom: 4, // Initial zoom level
            center: latlng,
            mapTypeId: google.maps.MapTypeId.ROADMAP, // Roadmap type
        };

        // Create the map instance
        mapRef.current = new google.maps.Map(document.getElementById('map_canvas'), myOptions);

        // If a route is provided, draw it
        if (route && route.length > 0) {
            const path = route.map((airport) => new google.maps.LatLng(airport.lat, airport.long));
            const polyline = new google.maps.Polyline({
                path,
                strokeColor: '#0000FF',
                strokeOpacity: 0.7,
                strokeWeight: 5,
            });
            polyline.setMap(mapRef.current);

            // Adjust the map bounds to fit the route
            const bounds = new google.maps.LatLngBounds();
            path.forEach((point) => bounds.extend(point));
            mapRef.current.fitBounds(bounds);
        }
    }, [route]);

    return (
        <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
            <div
                id="map_canvas"
                style={{
                    width: '100%',
                    height: '400px',
                    border: '1px solid #ccc',
                    borderRadius: '8px',
                }}
            ></div>
        </LoadScript>
    );
}

export default Map;
