import React, { useCallback, useRef } from 'react';
import { GoogleMap, LoadScript, Polyline } from '@react-google-maps/api';

const containerStyle = {
    width: '%',
    height: '400px',
    border: '1px solid #ccc',
    borderRadius: '8px',
};

const center = {
    lat: 39.8283, // Center of the contiguous US
    lng: -98.5795,
};

function Map({ route }) {
    const mapRef = useRef(null);

    const onLoad = useCallback((map) => {
        mapRef.current = map;
    }, []);

    const fitBounds = useCallback(() => {
        if (route && route.length > 0 && mapRef.current) {
            const bounds = new window.google.maps.LatLngBounds();
            route.forEach((airport) => {
                bounds.extend({ lat: airport.lat, lng: airport.long });
            });
            mapRef.current.fitBounds(bounds);
        }
    }, [route]);

    return (
        <LoadScript googleMapsApiKey="AIzaSyAWKY1m0h2NlZh6_WBgowVA87u516kWVfQ">
            <GoogleMap
                mapContainerStyle={containerStyle}
                center={center}
                zoom={4}
                onLoad={onLoad}
                onTilesLoaded={fitBounds}
                options={{
                    restriction: {
                        latLngBounds: {
                            north: 49.384358, // North boundary
                            south: 24.396308, // South boundary
                            west: -125.0, // West boundary
                            east: -66.93457, // East boundary
                        },
                        strictBounds: true, // Prevent dragging outside bounds
                    },
                }}
            >
                {/* Draw route if available */}
                {route && route.length > 0 && (
                    <Polyline
                        path={route.map((airport) => ({
                            lat: airport.lat,
                            lng: airport.long,
                        }))}
                        options={{
                            strokeColor: '#0000FF',
                            strokeOpacity: 0.7,
                            strokeWeight: 5,
                        }}
                    />
                )}
            </GoogleMap>
        </LoadScript>
    );
}

export default Map;
