import React, { useEffect, useRef } from 'react';
import L from 'leaflet';

function Map({ route }) {
    const mapRef = useRef(null); // Use a ref to persist the map instance

    useEffect(() => {
        if (!mapRef.current) {
            // Initialize the map only once
            mapRef.current = L.map('map', {
                center: [39.8283, -98.5795], // Center of the US
                zoom: 4,
                layers: [
                    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                        attribution: '© OpenStreetMap contributors',
                    }),
                ],
            });
        }

        const map = mapRef.current;
        const routeLayer = L.layerGroup().addTo(map); // Layer for the route

        // Draw route if provided
        if (route && route.length > 0) {
            const coordinates = route.map((airport) => [airport.lat, airport.long]);

            // Add polyline for the route
            const polyline = L.polyline(coordinates, {
                color: 'blue',
                weight: 5,
                opacity: 0.7,
            }).addTo(routeLayer);

            // Fit the map to the bounds of the route
            map.fitBounds(polyline.getBounds());
        }

        // Cleanup the route layer (not the map instance) on re-render
        return () => {
            routeLayer.clearLayers();
        };
    }, [route]);

    return (
        <div
            id="map"
            style={{
                height: '400px',
                width: '100%',
                border: '1px solid #ccc',
                borderRadius: '8px',
                overflow: 'hidden',
            }}
        ></div>
    );
}

export default Map;
