import React, { useState } from 'react';
import '../styles/ResultTable.css'; // Add a CSS file for styles
import airports from '../data/airports.json';

function ResultTable({ results }) {
    const [expandedRow, setExpandedRow] = useState(null);

    if (!results) return null;

    // Extract data from the results object
    const { userRoute, shortestPath, shortestDistance, alternativeRoutes } = results;

    // Helper function to format routes with just IATA codes
    const formatRouteIATA = (route) => route.join(' -> ');

    // Helper function to get detailed airport information
    const getAirportDetails = (route) => {
        return route.map((code) => {
            const airport = airports.find((airport) => airport.IATA === code);
            return airport
                ? `${code} - ${airport.NAME}, ${airport.CITY}, ${airport.STATE}`
                : `${code} - Unknown Airport`;
        });
    };

    // Prepare the rows for the table
    const userRouteRow = userRoute
        ? {
              route: formatRouteIATA(userRoute.route),
              distance: userRoute.distance,
              rawRoute: userRoute.route,
          }
        : null;

    const shortestPathRow = {
        route: formatRouteIATA(shortestPath),
        distance: shortestDistance,
        rawRoute: shortestPath,
    };

    const sortedAlternativeRoutes = alternativeRoutes
        .sort((a, b) => a.distance - b.distance) // Sort in ascending order of distance
        .map((route) => ({
            route: formatRouteIATA(route.route),
            distance: route.distance,
            rawRoute: route.route,
        }));

    // Combine all rows
    const tableRows = [
        ...(userRouteRow ? [userRouteRow] : []),
        shortestPathRow,
        ...sortedAlternativeRoutes,
    ];

    // Toggle expanded row
    const toggleRow = (index) => {
        setExpandedRow(expandedRow === index ? null : index);
    };

    return (
        <div className="result-table">
            <h2>All Route Results</h2>
            <div className="table-container">
                <table>
                    <thead>
                        <tr>
                            <th>Route</th>
                            <th>Distance (miles)</th>
                        </tr>
                        
                    </thead>
                    <tr>Filter</tr>
                    <tbody>
                        {tableRows.map((row, index) => (
                            <React.Fragment key={index}>
                                <tr onClick={() => toggleRow(index)}>
                                    <td>{row.route}</td>
                                    <td>{row.distance.toFixed(2)}</td>
                                </tr>
                                {expandedRow === index && (
                                    <tr>
                                        <td colSpan="2">
                                            <div className="details">
                                                <strong>Route Details:</strong>
                                                <ul>
                                                    {getAirportDetails(row.rawRoute).map((detail, idx) => (
                                                        <li key={idx}>{detail}</li>
                                                    ))}
                                                </ul>
                                            </div>
                                        </td>
                                    </tr>
                                )}
                            </React.Fragment>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ResultTable;
