import React, { useEffect, useState } from 'react';

function AirportDropdown({ label, onSelect }) {
    const [airports, setAirports] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredAirports, setFilteredAirports] = useState([]);

    useEffect(() => {
        // Load airport data from the backend
        fetch('http://localhost:5000/api/airports')
            .then((res) => {
                if (!res.ok) {
                    throw new Error(`HTTP error! status: ${res.status}`);
                }
                return res.json();
            })
            .then((data) => {
                // Sort airports alphabetically by NAME
                const sortedAirports = data.sort((a, b) => a.NAME.localeCompare(b.NAME));
                setAirports(sortedAirports);
                setFilteredAirports(sortedAirports);
            })
            .catch((err) => console.error('Failed to fetch airports:', err));
    }, []);

    // Update filtered airports based on search term
    useEffect(() => {
        if (searchTerm.trim() === '') {
            setFilteredAirports(airports);
        } else {
            const filtered = airports.filter((airport) =>
                `${airport.IATA} - ${airport.NAME}`.toLowerCase().includes(searchTerm.toLowerCase())
            );
            setFilteredAirports(filtered);
        }
    }, [searchTerm, airports]);

    return (
        <div className="dropdown">
            <label>{label}</label>
            {/* Search input */}
            <input
                type="text"
                placeholder="Search airports..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                    marginBottom: '10px',
                    padding: '8px',
                    width: '100%',
                    borderRadius: '4px',
                    border: '1px solid #ccc',
                }}
            />
            {/* Dropdown */}
            <select onChange={(e) => onSelect(e.target.value)} style={{ width: '100%', padding: '8px' }}>
                <option value="">Select an airport</option>
                {filteredAirports.map((airport) => (
                    <option key={airport.IATA} value={airport.IATA}>
                        {`${airport.IATA} - ${airport.NAME}`}
                    </option>
                ))}
            </select>
        </div>
    );
}

export default AirportDropdown;
