import React, { useState } from 'react';
import { LoadScript } from '@react-google-maps/api';
import AirportDropdown from './components/AirportDropdown';
import RouteForm from './components/RouteForm';
import Map from './components/Map';
import ResultTable from './components/ResultTable';
import airports from './data/airports.json'; // Import airport data for coordinates
import './styles.css';

function App() {
    const [start, setStart] = useState('');
    const [end, setEnd] = useState('');
    const [routeResults, setRouteResults] = useState(null);
    const [selectedRoute, setSelectedRoute] = useState([]);

    // Handle route validation and update map
    const handleRouteUpdate = (results) => {
        setRouteResults(results);

        // Extract the route coordinates if userRoute exists
        if (results?.userRoute?.route) {
            const routeCoordinates = results.userRoute.route.map((code) => {
                const airport = airports.find((airport) => airport.IATA === code);
                return airport ? { lat: airport.LAT, lng: airport.LONG } : null;
            }).filter(Boolean); // Remove null values

            setSelectedRoute(routeCoordinates);
        }
    };

    return (
        <LoadScript googleMapsApiKey="AIzaSyAWKY1m0h2NlZh6_WBgowVA87u516kWVfQ">
            <div className="container">
                <h1>Shortest Flight Path</h1>
                <div className="form-section">
                    <AirportDropdown label="Starting Airport" onSelect={setStart} />
                    <AirportDropdown label="Destination Airport" onSelect={setEnd} />
                    <RouteForm
                        start={start}
                        end={end}
                        onResults={handleRouteUpdate}
                    />
                </div>
                {routeResults && <ResultTable results={routeResults} />}
                <Map route={selectedRoute} />
            </div>
        </LoadScript>
    );
}

export default App;
