import React, { useCallback, useRef } from 'react';
import { GoogleMap, LoadScript, Polyline } from '@react-google-maps/api';

const containerStyle = {
    width: '50%',
    height: '400px'
};

function Map({ route }) {
    const mapRef = useRef(null);

    const onLoad = useCallback((map) => {
        mapRef.current = map;
    
        // Create bounds for the entire continental US
        const bounds = new window.google.maps.LatLngBounds(
            { lat: 10.396308, lng: -125.0 },   // Southwest corner
            { lat: 49.384358, lng: -66.93457 } // Northeast corner
        );
    
        // Fit the map to these bounds
        map.fitBounds(bounds);
    }, []);

    return (
        <LoadScript googleMapsApiKey="AIzaSyAWKY1m0h2NlZh6_WBgowVA87u516kWVfQ">
            <GoogleMap
                mapContainerStyle={containerStyle}
                onLoad={onLoad}
                options={{
                    disableDefaultUI: true, // Optional: removes default controls
                    zoomControl: true, // Enable zoom control
                    minZoom: 2, // Set minimum zoom level
                    maxZoom: 20, // Set maximum zoom level
                }}
            >
                {/* Route drawing logic remains the same */}
                {route && route.length > 0 && (
                    <Polyline
                        path={route.map((airport) => ({
                            lat: airport.lat,
                            lng: airport.long,
                        }))}
                        options={{
                            strokeColor: '#0000FF',
                            strokeOpacity: 0.7,
                            strokeWeight: 5,
                        }}
                    />
                )}
            </GoogleMap>
        </LoadScript>
    );
}

export default Map;
