import React, { useState } from 'react';
import AirportDropdown from './components/AirportDropdown';
import RouteForm from './components/RouteForm';
import Map from './components/Map';
import ResultTable from './components/ResultTable';

function App() {
    const [start, setStart] = useState(''); // Starting airport code
    const [end, setEnd] = useState(''); // Destination airport code
    const [routeResults, setRouteResults] = useState(null); // Route results from backend

    return (
        <div className="container">
            <h1>Shortest Flight Path</h1>
            <div className="form-section">
                {/* Dropdown for selecting starting airport */}
                <AirportDropdown label="Starting Airport" onSelect={setStart} />
                
                {/* Dropdown for selecting destination airport */}
                <AirportDropdown label="Destination Airport" onSelect={setEnd} />
                
                {/* Form for submitting route */}
                <RouteForm
                    start={start}
                    end={end}
                    onResults={(results) => setRouteResults(results)} // Handle results
                />
            </div>

            {/* Display the result table if route results exist */}
            {routeResults && <ResultTable results={routeResults} />}

            {/* Display the map if route results exist */}
            <Map route={routeResults?.path} />
        </div>
    );
}

export default App;
